import React from 'react';
import CameraIcon from "../images/camera.png"
import UploadIcon from "../images/upload.png"

function PhotoOptions() {
  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      console.log('Selected file:', file);
    }
  };

  const handleTakePhoto = () => {
    console.log('Take photo button clicked');
    // Implement photo-taking functionality here, or use a library like react-webcam
  };

  return (
    <div className="App">
      <div className="button-container">
        <div className="icon-button" onClick={handleTakePhoto}>
          <img src={Camera-I} alt="camera icon" />
          <p>Take photo</p>
        </div>
        <div className="icon-button">
          <input 
            type="file" 
            accept="image/*" 
            onChange={handleFileChange}
            style={{ display: 'none' }} 
            id="upload-image" 
          />
          <label htmlFor="upload-image" className="icon-button">
            <img src="/upload-icon.svg" alt="upload icon" />
            <p>Upload an image</p>
          </label>
        </div>
      </div>
    </div>
  );
}

export default PhotoOptions;
